window.onload= function(){

  $('#deleteModalCarrito').on('show.bs.modal', function (event) {
      var button = $(event.relatedTarget) // Button that triggered the modal
      var name = button.data('name') // Extract info from data-* attributes
      var id = button.data('id') // Extract info from data-* attributes
      // If necessary, you could initiate an AJAX request here (and then do the updating in a callback).
      // Update the modal's content. We'll use jQuery here, but you could use a data binding library or other methods instead.
      var modal = $(this)
      modal.find('.modal-title span').text(name)

      $("#deleteCarrito").attr("href","/carrito_delete/"+id)

      
  })

  $('#comprarModalCarrito').on('show.bs.modal', function (event) {
    var button = $(event.relatedTarget) // Button that triggered the modal
    // If necessary, you could initiate an AJAX request here (and then do the updating in a callback).
    $("#comprarCarrito").attr("href","/factura_create")
  })

  $("[type='number']").keypress(function (evt) {
      evt.preventDefault();
  });

}